<script>
import { gsap } from 'gsap'
import { onMounted, ref } from 'vue'

export default {
  setup () {
    const board = ref()
    const beerCan = ref()
    const beerCanData = ref(1)
    const flash = ref()
    const smoke = ref()
    const tvLight = ref()
    const boardAni = () => {
      gsap.to('#board', {
        rotate: '+=120',
        transformOrigin: 'center center',
        duration: 1,
        ease: 'bounce',
        onStart () {
          beerCan.value.style.pointerEvents = 'none'
        },
        onComplete () {
          beerCan.value.style.pointerEvents = 'auto'
        }
      })
    }

    const rotateBoard = () => {
      if (beerCanData.value === 1) {
        boardAni()
        beerCanData.value = 2
      } else if (beerCanData.value === 2) {
        boardAni()
        beerCanData.value = 3
      } else if (beerCanData.value === 3) {
        boardAni()
        beerCanData.value = 1
      }
    }
    onMounted(() => {
      gsap.to(flash.value, {
        rotate: -360,
        transformOrigin: 'center center',
        duration: 10,
        ease: 'none',
        repeat: -1
      })
      gsap.to(smoke.value, {
        scaleY: 2.2,
        opacity: 0,
        transformOrigin: 'bottom',
        duration: 2,
        ease: 'none',
        repeat: -1
      })
      gsap.from(tvLight.value, {
        opacity: 0,
        duration: 0.5,
        ease: 'none',
        repeat: -1,
        repeatDelay: 3
      })
    })

    return {
      board,
      beerCan,
      beerCanData,
      flash,
      smoke,
      tvLight,
      boardAni,
      rotateBoard
    }
  }
}
</script>

