<script>
import { onMounted } from 'vue'
import gsap from 'gsap'
import { MotionPathPlugin } from 'gsap/MotionPathPlugin'
gsap.registerPlugin(MotionPathPlugin)
export default {
  setup () {
    const rx = window.innerWidth / 200
    const ry = window.innerHeight / 200
    const path = [
      // 1
      { x: 80, y: 20 },
      { x: 90, y: 2 },
      { x: 110, y: 10 },
      // 2
      { x: 100, y: 20 },
      { x: 90, y: 2 },
      { x: 1, y: 50 },
      // 3
      { x: 10, y: 30 },
      { x: 50, y: 40 },
      { x: 100, y: 20 }
    ]
    const scaledPath = path.map(({ x, y }) => {
      return {
        x: x * rx,
        y: y * ry
      }
    })
    onMounted(() => {
      gsap.to('#rect', {
        duration: 5,
        motionPath: {
          path: scaledPath,
          align: scaledPath,
          autoRotate: true,
          alignOrigin: [0.5, 0.5]
        }
      })
    })
  }
}
</script>

